ruleSearchList:
@js:
var list = [];
if (/search/.test(baseUrl)) {
  list = org.jsoup.Jsoup.parse(JSON.parse(result).results).select('.col-12').toArray()
} else {
  list = org.jsoup.Jsoup.parse(result).select('.col-12').toArray()
}
list

ruleSearchUrlNext:
@js:
var result=java.aJax(baseUrl);
if(/\?s=/.test(baseUrl)){
var next=JSON.parse(java.base64Decoder(org.jsoup.Jsoup.parse(result).select('#site-args').html())).next
}else if(/search/.test(baseUrl)){
var next=JSON.parse(result).next
}
next
